import type { Express } from "express";
import { createServer, type Server } from "http";
import { storage } from "./storage";
import { insertCoralDataSchema, insertTankCompositionSchema } from "@shared/schema";
import { z } from "zod";

export async function registerRoutes(app: Express): Promise<Server> {
  // Google Sheets integration
  app.post("/api/sheets/connect", async (req, res) => {
    try {
      const { sheetsUrl } = req.body;

      if (!sheetsUrl) {
        return res.status(400).json({ message: "Google Sheets URL is required" });
      }

      // Extract sheet ID from URL (handles both edit and pubhtml URLs)
      const sheetIdMatch = sheetsUrl.match(/\/spreadsheets\/d\/([a-zA-Z0-9-_]+)/);
      if (!sheetIdMatch) {
        return res.status(400).json({ message: "Invalid Google Sheets URL format" });
      }

      const sheetId = sheetIdMatch[1];
      const csvUrl = `https://docs.google.com/spreadsheets/d/${sheetId}/export?format=csv&gid=0`;

      // Fetch data from Google Sheets
      const response = await fetch(csvUrl);
      if (!response.ok) {
        return res.status(400).json({ message: "Failed to fetch data from Google Sheets" });
      }

      const csvData = await response.text();
      const rows = csvData.split('\n').slice(1); // Skip header row

      // Clear existing coral data
      await storage.clearCoralData();

      // Parse CSV and store coral data
      const coralData = [];
      for (const row of rows) {
        if (row.trim()) {
          // Handle CSV parsing with proper comma splitting (accounting for quoted values)
          const columns = row.match(/(".*?"|[^",\s]+)(?=\s*,|\s*$)/g) || [];
          const cleanColumns = columns.map(col => col.replace(/^"|"$/g, '').trim());

          if (cleanColumns.length >= 6) {
            const data = {
              name: cleanColumns[1] || '', // Column B
              fullImageUrl: cleanColumns[2] || '', // Column C  
              thumbnailUrl: cleanColumns[3] || '', // Column D
              width: parseInt(cleanColumns[4]) || 100, // Column E
              height: parseInt(cleanColumns[5]) || 100, // Column F
            };

            if (data.name && data.fullImageUrl && data.thumbnailUrl) {
              const coral = await storage.createCoralData(data);
              coralData.push(coral);
            }
          }
        }
      }

      res.json({ 
        success: true, 
        message: `Successfully loaded ${coralData.length} coral specimens`,
        data: coralData 
      });
    } catch (error) {
      console.error('Error connecting to Google Sheets:', error);
      res.status(500).json({ message: "Failed to connect to Google Sheets" });
    }
  });

  // Get all coral data
  app.get("/api/corals", async (req, res) => {
    try {
      const corals = await storage.getAllCoralData();
      res.json(corals);
    } catch (error) {
      console.error('Error fetching corals:', error);
      res.status(500).json({ message: "Failed to fetch coral data" });
    }
  });

  // Save tank composition
  app.post("/api/compositions", async (req, res) => {
    try {
      const validatedData = insertTankCompositionSchema.parse(req.body);
      const composition = await storage.saveTankComposition(validatedData);
      res.json(composition);
    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({ message: "Invalid composition data", errors: error.errors });
      }
      console.error('Error saving composition:', error);
      res.status(500).json({ message: "Failed to save composition" });
    }
  });

  // Get tank composition
  app.get("/api/compositions/:id", async (req, res) => {
    try {
      const { id } = req.params;
      const composition = await storage.getTankComposition(id);

      if (!composition) {
        return res.status(404).json({ message: "Composition not found" });
      }

      res.json(composition);
    } catch (error) {
      console.error('Error fetching composition:', error);
      res.status(500).json({ message: "Failed to fetch composition" });
    }
  });

  const httpServer = createServer(app);
  return httpServer;
}

server/storage.ts.content:
import { type CoralData, type InsertCoralData, type TankComposition, type InsertTankComposition } from "@shared/schema";
import { randomUUID } from "crypto";

export interface IStorage {
  // Coral data methods
  getAllCoralData(): Promise<CoralData[]>;
  createCoralData(data: InsertCoralData): Promise<CoralData>;
  clearCoralData(): Promise<void>;

  // Tank composition methods
  saveTankComposition(composition: InsertTankComposition): Promise<TankComposition>;
  getTankComposition(id: string): Promise<TankComposition | undefined>;
}

export class MemStorage implements IStorage {
  private coralData: Map<string, CoralData>;
  private tankCompositions: Map<string, TankComposition>;

  constructor() {
    this.coralData = new Map();
    this.tankCompositions = new Map();
  }

  async getAllCoralData(): Promise<CoralData[]> {
    return Array.from(this.coralData.values());
  }

  async createCoralData(insertData: InsertCoralData): Promise<CoralData> {
    const id = randomUUID();
    const data: CoralData = { ...insertData, id };
    this.coralData.set(id, data);
    return data;
  }

  async clearCoralData(): Promise<void> {
    this.coralData.clear();
  }

  async saveTankComposition(insertComposition: InsertTankComposition): Promise<TankComposition> {
    const id = randomUUID();
    const composition: TankComposition = { ...insertComposition, id };
    this.tankCompositions.set(id, composition);
    return composition;
  }

  async getTankComposition(id: string): Promise<TankComposition | undefined> {
    return this.tankCompositions.get(id);
  }
}

export const storage = new MemStorage();

server/vite.ts.content:
import express, { type Express } from "express";
import fs from "fs";
import path from "path";
import { createServer as createViteServer, createLogger } from "vite";
import { type Server } from "http";
import viteConfig from "../vite.config";
import { nanoid } from "nanoid";

const viteLogger = createLogger();

export function log(message: string, source = "express") {
  const formattedTime = new Date().toLocaleTimeString("en-US", {
    hour: "numeric",
    minute: "2-digit",
    second: "2-digit",
    hour12: true,
  });

  console.log(`${formattedTime} [${source}] ${message}`);
}

export async function setupVite(app: Express, server: Server) {
  const serverOptions = {
    middlewareMode: true,
    hmr: { server },
    allowedHosts: true as const,
  };

  const vite = await createViteServer({
    ...viteConfig,
    configFile: false,
    customLogger: {
      ...viteLogger,
      error: (msg, options) => {
        viteLogger.error(msg, options);
        process.exit(1);
      },
    },
    server: serverOptions,
    appType: "custom",
  });

  app.use(vite.middlewares);
  app.use("*", async (req, res, next) => {
    const url = req.originalUrl;

    try {
      const clientTemplate = path.resolve(
        import.meta.dirname,
        "..",
        "client",
        "index.html",
      );

      // always reload the index.html file from disk incase it changes
      let template = await fs.promises.readFile(clientTemplate, "utf-8");
      template = template.replace(
        `src="/src/main.tsx"`,
        `src="/src/main.tsx?v=${nanoid()}"`,
      );
      const page = await vite.transformIndexHtml(url, template);
      res.status(200).set({ "Content-Type": "text/html" }).end(page);
    } catch (e) {
      vite.ssrFixStacktrace(e as Error);
      next(e);
    }
  });
}

export function serveStatic(app: Express) {
  const distPath = path.resolve(import.meta.dirname, "public");

  if (!fs.existsSync(distPath)) {
    throw new Error(
      `Could not find the build directory: ${distPath}, make sure to build the client first`,
    );
  }

  app.use(express.static(distPath));

  // fall through to index.html if the file doesn't exist
  app.use("*", (_req, res) => {
    res.sendFile(path.resolve(distPath, "index.html"));
  });
}

shared/schema.ts.content:
import { sql } from "drizzle-orm";
import { pgTable, text, varchar, integer, real, boolean } from "drizzle-orm/pg-core";
import { createInsertSchema } from "drizzle-zod";
import { z } from "zod";

export const coralData = pgTable("coral_data", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  name: text("name").notNull(),
  fullImageUrl: text("full_image_url").notNull(),
  thumbnailUrl: text("thumbnail_url").notNull(),
  width: integer("width").notNull(),
  height: integer("height").notNull(),
});

export const tankCompositions = pgTable("tank_compositions", {
  id: varchar("id").primaryKey().default(sql`gen_random_uuid()`),
  baseImageUrl: text("base_image_url").notNull(),
  overlays: text("overlays").notNull(), // JSON string of overlay data
  watermarkPosition: text("watermark_position").notNull(),
});

export const insertCoralDataSchema = createInsertSchema(coralData).omit({ id: true });
export const insertTankCompositionSchema = createInsertSchema(tankCompositions).omit({ id: true });

export type InsertCoralData = z.infer<typeof insertCoralDataSchema>;
export type CoralData = typeof coralData.$inferSelect;
export type InsertTankComposition = z.infer<typeof insertTankCompositionSchema>;
export type TankComposition = typeof tankCompositions.$inferSelect;

// Client-side types for overlay management
export interface OverlayData {
  id: string;
  coralId: string;
  name: string;
  imageUrl: string;
  x: number;
  y: number;
  width: number;
  height: number;
  opacity: number;
  layer: number;
  rotation?: number;
  flipH?: boolean;
  flipV?: boolean;
}

export interface CanvasState {
  baseImage: string | null;
  overlays: OverlayData[];
  selectedOverlayId: string | null;
  zoom: number;
  panX: number;
  panY: number;
}

client/src/App.tsx.content:
import { Switch, Route } from "wouter";
import { queryClient } from "./lib/queryClient";
import { QueryClientProvider } from "@tanstack/react-query";
import { Toaster } from "@/components/ui/toaster";
import { TooltipProvider } from "@/components/ui/tooltip";
import { ThemeProvider } from "@/components/theme-provider";
import AquariumDesigner from "@/pages/aquarium-designer";
import NotFound from "@/pages/not-found";

function Router() {
  return (
    <Switch>
      <Route path="/" component={AquariumDesigner} />
      <Route component={NotFound} />
    </Switch>
  );
}

function App() {
  return (
    <ThemeProvider defaultTheme="dark" storageKey="coralscape-ui-theme">
      <QueryClientProvider client={queryClient}>
        <TooltipProvider>
          <Toaster />
          <Router />
        </TooltipProvider>
      </QueryClientProvider>
    </ThemeProvider>
  );
}

export default App;

