@layer base {
  * {
    @apply border-border;
  }

  body {
    @apply font-sans antialiased bg-background text-foreground;
    font-family: 'Inter', system-ui, sans-serif;
  }
}

/* Custom styles for aquarium designer */
.canvas-workspace {
  touch-action: none; /* Disable default touch behaviors for better custom handling */
}

.canvas-overlay {
  position: absolute;
  cursor: move;
  user-select: none;
  touch-action: none;
}

.canvas-overlay.selected {
  outline: 2px solid hsl(207, 90%, 54%);
  outline-offset: 2px;
}

.canvas-overlay .resize-handle {
  position: absolute;
  width: 12px;
  height: 12px;
  background: hsl(207, 90%, 54%);
  border: 2px solid white;
  border-radius: 50%;
  opacity: 0;
  transition: opacity 0.2s;
}

/* Larger touch targets for mobile devices */
@media (hover: none) and (pointer: coarse) {
  .canvas-overlay .resize-handle {
    width: 20px;
    height: 20px;
  }

  .canvas-overlay .resize-handle.nw {
    top: -10px;
    left: -10px;
  }

  .canvas-overlay .resize-handle.ne {
    top: -10px;
    right: -10px;
  }

  .canvas-overlay .resize-handle.sw {
    bottom: -10px;
    left: -10px;
  }

  .canvas-overlay .resize-handle.se {
    bottom: -10px;
    right: -10px;
  }
}

.canvas-overlay:hover .resize-handle,
.canvas-overlay.selected .resize-handle {
  opacity: 1;
}

.canvas-overlay .resize-handle.nw {
  top: -6px;
  left: -6px;
  cursor: nw-resize;
}

.canvas-overlay .resize-handle.ne {
  top: -6px;
  right: -6px;
  cursor: ne-resize;
}

.canvas-overlay .resize-handle.sw {
  bottom: -6px;
  left: -6px;
  cursor: sw-resize;
}

.canvas-overlay .resize-handle.se {
  bottom: -6px;
  right: -6px;
  cursor: se-resize;
}

.drag-preview {
  opacity: 0.8;
  transform: rotate(5deg);
  pointer-events: none;
}

.drop-zone-active {
  border-color: hsl(207, 90%, 54%) !important;
  background-color: hsl(207, 90%, 97%) !important;
}

/* Mobile and tablet scroll bar - shifts content instead of overlaying */
@media (max-width: 1200px) {
  /* Hide all webkit scrollbars completely */
  ::-webkit-scrollbar {
    display: none !important;
    width: 0px !important;
    height: 0px !important;
    background: transparent !important;
  }

  html {
    scrollbar-width: none !important;
    -ms-overflow-style: none !important;
    overflow-y: scroll !important; /* Force scroll space reservation */
  }

  body {
    padding-right: 20px !important; /* Static padding for scroll indicator space */
    touch-action: pan-y; /* Allow vertical scrolling but prevent zoom */
    -webkit-text-size-adjust: 100%; /* Prevent text size adjustment */
    -webkit-touch-callout: none; /* Disable callout */
    -webkit-user-select: none; /* Disable selection */
    user-select: none;
    scrollbar-width: none !important;
    -ms-overflow-style: none !important;
  }

  /* Prevent zoom on form elements */
  input, select, textarea {
    font-size: 16px !important; /* Prevent zoom on focus */
    touch-action: manipulation;
  }

  .mobile-scroll-indicator {
    position: fixed !important;
    top: 0 !important;
    right: 0 !important;
    width: 20px !important;
    min-width: 20px !important;
    max-width: 20px !important;
    height: 100vh !important;
    background: white !important;
    z-index: 9999 !important;
    pointer-events: auto !important;
    opacity: 1 !important;
    border-left: 1px solid rgba(0, 0, 0, 0.1) !important;
    box-sizing: border-box !important;
  }

  .mobile-scroll-indicator::before {
    content: '' !important;
    position: absolute !important;
    top: 50% !important;
    left: 50% !important;
    transform: translate(-50%, -50%) !important;
    width: 4px !important;
    height: 50px !important;
    background: rgba(0, 0, 0, 0.4) !important;
    border-radius: 2px !important;
  }

  /* Ensure no hover effects or dynamic changes */
  .mobile-scroll-indicator:hover,
  .mobile-scroll-indicator:active,
  .mobile-scroll-indicator:focus {
    width: 20px !important;
    background: white !important;
    opacity: 1 !important;
  }
}

.watermark {
  pointer-events: none;
  user-select: none;
}

