import { CanvasState, OverlayData, CoralData } from "@shared/schema";
import { convertHeicToJpeg, isSupportedImageFile } from "@/lib/heic-converter";
import { useToast } from "@/hooks/use-toast";

interface CanvasWorkspaceProps {
  canvasState: CanvasState;
  onUpdateOverlay: (overlayId: string, updates: Partial<OverlayData>) => void;
  onSelectOverlay: (overlayId: string | null) => void;
  onBaseImageUpload: (imageUrl: string) => void;
  onAddOverlay: (coral: CoralData, position: { x: number; y: number }) => void;
  onZoomChange: (zoom: number) => void;
  onPanChange: (panX: number, panY: number) => void;
  onDeleteOverlay: (overlayId: string) => void;
  onUndo: () => void;
  canUndo: boolean;
  onDragStart: (overlayId: string) => void;
  onDragEnd: () => void;
}

interface DraggableOverlayProps {
  overlay: OverlayData;
  isSelected: boolean;
  onUpdate: (updates: Partial<OverlayData>) => void;
  onSelect: () => void;
  onDelete: () => void;
  onDragStart: () => void;
  onDragEnd: () => void;
  zoom: number;
}

interface TransformControls {
  rotation: number;
  flipH: boolean;
  flipV: boolean;
}

function DraggableOverlay({ overlay, isSelected, onUpdate, onSelect, onDelete, onDragStart, onDragEnd, zoom }: DraggableOverlayProps) {
  const [isDragging, setIsDragging] = useState(false);
  const [isResizing, setIsResizing] = useState(false);
  const [dragStart, setDragStart] = useState({ x: 0, y: 0 });
  const [resizeStart, setResizeStart] = useState({ x: 0, y: 0, width: 0, height: 0 });

  const handleMouseDown = useCallback((e: React.MouseEvent) => {
    if (e.target !== e.currentTarget && !(e.target as HTMLElement).classList.contains('overlay-image')) {
      return; // Don't start drag if clicking on resize handle
    }

    e.preventDefault();
    e.stopPropagation();
    onSelect();
    setIsDragging(true);
    setDragStart({ x: e.clientX - overlay.x, y: e.clientY - overlay.y });
    onDragStart(); // Notify parent that drag started
  }, [overlay.x, overlay.y, onSelect, onDragStart]);

  const handleTouchStart = useCallback((e: React.TouchEvent) => {
    if (e.target !== e.currentTarget && !(e.target as HTMLElement).classList.contains('overlay-image')) {
      return; // Don't start drag if touching resize handle
    }

    e.preventDefault();
    e.stopPropagation();
    onSelect();
    setIsDragging(true);
    const touch = e.touches[0];
    setDragStart({ x: touch.clientX - overlay.x, y: touch.clientY - overlay.y });
    onDragStart(); // Notify parent that drag started
  }, [overlay.x, overlay.y, onSelect, onDragStart]);

  const handleResizeMouseDown = useCallback((e: React.MouseEvent, direction: string) => {
    e.preventDefault();
    e.stopPropagation();
    setIsResizing(true);
    setResizeStart({
      x: e.clientX,
      y: e.clientY,
      width: overlay.width,
      height: overlay.height,
    });
  }, [overlay.width, overlay.height]);

  const handleResizeTouchStart = useCallback((e: React.TouchEvent, direction: string) => {
    e.preventDefault();
    e.stopPropagation();
    setIsResizing(true);
    const touch = e.touches[0];
    setResizeStart({
      x: touch.clientX,
      y: touch.clientY,
      width: overlay.width,
      height: overlay.height,
    });
  }, [overlay.width, overlay.height]);

  // Calculate aspect ratio for the overlay
  const aspectRatio = overlay.width / overlay.height;

  const handleMouseMove = useCallback((e: MouseEvent) => {
    if (isDragging) {
      // Apply mobile zoom dampening - 50% slower movement when zoomed in on mobile
      const isMobile = window.innerWidth <= 1024;
      const zoomDampening = isMobile && zoom > 1 ? 0.5 : 1;

      let newX = e.clientX - dragStart.x;
      let newY = e.clientY - dragStart.y;

      // Apply dampening by reducing movement delta
      if (zoomDampening < 1) {
        const currentX = overlay.x;
        const currentY = overlay.y;
        newX = currentX + (newX - currentX) * zoomDampening;
        newY = currentY + (newY - currentY) * zoomDampening;
      }

      onUpdate({
        x: Math.max(0, newX),
        y: Math.max(0, newY),
      });
    } else if (isResizing) {
      const deltaX = e.clientX - resizeStart.x;
      const deltaY = e.clientY - resizeStart.y;

      // Maintain aspect ratio when resizing
      const newWidth = Math.max(20, resizeStart.width + deltaX);
      const newHeight = newWidth / aspectRatio;

      onUpdate({
        width: newWidth,
        height: newHeight,
      });
    }
  }, [isDragging, isResizing, dragStart, resizeStart, onUpdate, aspectRatio, zoom, overlay.x, overlay.y]);

  const handleTouchMove = useCallback((e: TouchEvent) => {
    e.preventDefault(); // Prevent scrolling
    const touch = e.touches[0];
    if (isDragging) {
      // Apply mobile zoom dampening - 50% slower movement when zoomed in on mobile
      const isMobile = window.innerWidth <= 1024;
      const zoomDampening = isMobile && zoom > 1 ? 0.5 : 1;

      let newX = touch.clientX - dragStart.x;
      let newY = touch.clientY - dragStart.y;

      // Apply dampening by reducing movement delta
      if (zoomDampening < 1) {
        const currentX = overlay.x;
        const currentY = overlay.y;
        newX = currentX + (newX - currentX) * zoomDampening;
        newY = currentY + (newY - currentY) * zoomDampening;
      }

      onUpdate({
        x: Math.max(0, newX),
        y: Math.max(0, newY),
      });
    } else if (isResizing) {
      const deltaX = touch.clientX - resizeStart.x;
      const deltaY = touch.clientY - resizeStart.y;

      // Maintain aspect ratio when resizing
      const newWidth = Math.max(20, resizeStart.width + deltaX);
      const newHeight = newWidth / aspectRatio;

      onUpdate({
        width: newWidth,
        height: newHeight,
      });
    }
  }, [isDragging, isResizing, dragStart, resizeStart, onUpdate, aspectRatio, zoom, overlay.x, overlay.y]);

  const handleMouseUp = useCallback(() => {
    const wasDragging = isDragging;
    setIsDragging(false);
    setIsResizing(false);
    if (wasDragging) {
      onDragEnd(); // Notify parent that drag ended
    }
  }, [isDragging, onDragEnd]);

  const handleTouchEnd = useCallback(() => {
    const wasDragging = isDragging;
    setIsDragging(false);
    setIsResizing(false);
    if (wasDragging) {
      onDragEnd(); // Notify parent that drag ended
    }
  }, [isDragging, onDragEnd]);

  // Add event listeners
  React.useEffect(() => {
    if (isDragging || isResizing) {
      document.addEventListener('mousemove', handleMouseMove);
      document.addEventListener('mouseup', handleMouseUp);
      document.addEventListener('touchmove', handleTouchMove, { passive: false });
      document.addEventListener('touchend', handleTouchEnd);

      return () => {
        document.removeEventListener('mousemove', handleMouseMove);
        document.removeEventListener('mouseup', handleMouseUp);
        document.removeEventListener('touchmove', handleTouchMove);
        document.removeEventListener('touchend', handleTouchEnd);
      };
    }
  }, [isDragging, isResizing, handleMouseMove, handleMouseUp, handleTouchMove, handleTouchEnd]);

  return (
    <div
      className={`canvas-overlay ${isSelected ? 'selected' : ''}`}
      style={{
        left: overlay.x,
        top: overlay.y,
        width: overlay.width,
        height: overlay.height,
        opacity: overlay.opacity,
        zIndex: overlay.layer + 10,
        touchAction: 'none'
      }}
      onMouseDown={handleMouseDown}
      onTouchStart={handleTouchStart}
    >
      <img
        src={overlay.imageUrl}
        alt={overlay.name}
        className="overlay-image w-full h-full object-cover rounded-lg border-2 border-transparent hover:border-primary transition-colors"
        style={{
          transform: `rotate(${overlay.rotation || 0}deg) scaleX(${overlay.flipH ? -1 : 1}) scaleY(${overlay.flipV ? -1 : 1})`,
          touchAction: 'none'
        }}
        title={overlay.name}
        draggable={false}
        onError={(e) => {
          const target = e.target as HTMLImageElement;
          target.src = 'data:image/svg+xml;base64,PHN2ZyB3aWR0aD0iMTAwIiBoZWlnaHQ9IjEwMCIgdmlld0JveD0iMCAwIDEwMCAxMDAiIGZpbGw9Im5vbmUiIHhtbG5zPSJodHRwOi8vd3d3LnczLm9yZy8yMDAwL3N2ZyI+CjxyZWN0IHdpZHRoPSIxMDAiIGhlaWdodD0iMTAwIiBmaWxsPSIjRjNGNEY2Ii8+CjxwYXRoIGQ9Ik00MCA0MEg2MFY2MEg0MFY0MFoiIGZpbGw9IiM5Q0EzQUYiLz4KPC9zdmc+';
        }}
      />

      {isSelected && (
        <>
          <div
            className="resize-handle nw"
            onMouseDown={(e) => handleResizeMouseDown(e, 'nw')}
            onTouchStart={(e) => handleResizeTouchStart(e, 'nw')}
          />
          <div
            className="resize-handle ne"
            onMouseDown={(e) => handleResizeMouseDown(e, 'ne')}
            onTouchStart={(e) => handleResizeTouchStart(e, 'ne')}
          />
          <div
            className="resize-handle sw"
            onMouseDown={(e) => handleResizeMouseDown(e, 'sw')}
            onTouchStart={(e) => handleResizeTouchStart(e, 'sw')}
          />
          <div
            className="resize-handle se"
            onMouseDown={(e) => handleResizeMouseDown(e, 'se')}
            onTouchStart={(e) => handleResizeTouchStart(e, 'se')}
          />

          {/* Transform Controls */}
          <div className="absolute -top-16 left-1/2 transform -translate-x-1/2 bg-background border border-border rounded-lg shadow-lg p-2 flex items-center space-x-2">
            <div className="flex items-center space-x-1">
              <div
                className="relative cursor-pointer"
                onMouseDown={(e) => {
                  e.preventDefault();
                  const startRotation = overlay.rotation || 0;
                  const rect = (e.currentTarget as HTMLElement).getBoundingClientRect();
                  const centerX = rect.left + rect.width / 2;
                  const centerY = rect.top + rect.height / 2;
                  const startAngle = Math.atan2(e.clientY - centerY, e.clientX - centerX);

                  let isDragging = true;

                  const handleMouseMove = (moveEvent: MouseEvent) => {
                    if (!isDragging) return;

                    const currentAngle = Math.atan2(moveEvent.clientY - centerY, moveEvent.clientX - centerX);
                    const angleDiff = (currentAngle - startAngle) * (180 / Math.PI) * 0.3; // Reduced sensitivity by 70%
                    const newRotation = (startRotation + angleDiff) % 360;

                    onUpdate({ rotation: newRotation < 0 ? newRotation + 360 : newRotation });
                  };

                  const handleMouseUp = () => {
                    isDragging = false;
                    document.removeEventListener('mousemove', handleMouseMove);
                    document.removeEventListener('mouseup', handleMouseUp);
                  };

                  document.addEventListener('mousemove', handleMouseMove);
                  document.addEventListener('mouseup', handleMouseUp);
                }}
                onClick={() => onUpdate({ rotation: ((overlay.rotation || 0) + 10) % 360 })}
                title="Drag to rotate smoothly, click for 10Â°"
              >
                <div className="w-6 h-6 flex items-center justify-center border border-border rounded hover:bg-muted transition-colors">
                  <RotateCw className="h-3 w-3 text-foreground" />
                </div>
              </div>
              <span className="text-xs text-muted-foreground min-w-[35px]">
                {Math.round(overlay.rotation || 0)}Â°
              </span>
            </div>
            <div className="h-4 w-px bg-border"></div>
            <Button
              size="sm"
              variant="ghost"
              className="h-6 w-6 p-0 text-foreground hover:bg-muted"
              onClick={() => onUpdate({ 
                flipH: !(overlay.flipH || false)
              })}
              title="Flip Horizontal"
            >
              <FlipHorizontal className="h-3 w-3" />
            </Button>
            <Button
              size="sm"
              variant="ghost"
              className="h-6 w-6 p-0 text-foreground hover:bg-muted"
              onClick={() => onUpdate({ 
                flipV: !(overlay.flipV || false)
              })}
              title="Flip Vertical"
            >
              <FlipVertical className="h-3 w-3" />
            </Button>
            <div className="h-4 w-px bg-border"></div>
            <Button
              size="sm"
              variant="ghost"
              className="h-6 w-6 p-0 text-red-600 hover:text-red-700 hover:bg-red-50 dark:text-red-400 dark:hover:text-red-300 dark:hover:bg-red-900/20"
              onClick={onDelete}
              title="Delete Layer"
            >
              <Trash2 className="h-3 w-3" />
            </Button>
          </div>
        </>
      )}
    </div>
  );
}

export default function CanvasWorkspace({
  canvasState,
  onUpdateOverlay,
  onSelectOverlay,
  onBaseImageUpload,
  onAddOverlay,
  onZoomChange,
  onPanChange,
  onDeleteOverlay,
  onUndo,
  canUndo,
  onDragStart,
  onDragEnd,
}: CanvasWorkspaceProps) {
  const fileInputRef = useRef<HTMLInputElement>(null);
  const canvasRef = useRef<HTMLDivElement>(null);
  const [isEditingZoom, setIsEditingZoom] = useState(false);
  const [zoomInput, setZoomInput] = useState(Math.round(canvasState.zoom * 100).toString());
  const [isPanning, setIsPanning] = useState(false);
  const [panStart, setPanStart] = useState({ x: 0, y: 0 });

  const [{ isOver }, drop] = useDrop(() => ({
    accept: 'coral',
    drop: (item: { coral: CoralData }, monitor) => {
      const canvasRect = canvasRef.current?.getBoundingClientRect();
      const dropOffset = monitor.getClientOffset();

      if (canvasRect && dropOffset) {
        const x = dropOffset.x - canvasRect.left;
        const y = dropOffset.y - canvasRect.top;
        onAddOverlay(item.coral, { x, y });
      }
    },
    collect: (monitor) => ({
      isOver: monitor.isOver(),
    }),
  }));

  const { toast } = useToast();

  const handleFileUpload = async (event: React.ChangeEvent<HTMLInputElement>) => {
    const file = event.target.files?.[0];
    if (!file) return;

    // Check if file is supported
    if (!isSupportedImageFile(file)) {
      toast({
        title: "Unsupported File Format",
        description: "Please upload a JPEG, PNG, GIF, WebP, or HEIC image file.",
        variant: "destructive",
      });
      return;
    }

    try {
      // Convert HEIC files or read regular images
      const dataUrl = await convertHeicToJpeg(file);
      onBaseImageUpload(dataUrl);

      if (file.name.toLowerCase().includes('.heic') || file.name.toLowerCase().includes('.heif')) {
        toast({
          title: "HEIC File Converted",
          description: "Your HEIC image has been successfully converted and uploaded.",
        });
      }
    } catch (error) {
      console.error('File upload error:', error);
      toast({
        title: "Upload Failed",
        description: error instanceof Error ? error.message : "Failed to upload image. Please try again.",
        variant: "destructive",
      });
    }
  };

  const handleCanvasClick = (e: React.MouseEvent) => {
    e.preventDefault(); // Prevent mobile zoom on double-tap
    if (e.target === e.currentTarget) {
      onSelectOverlay(null);
    }
  };

  const handleMouseDown = useCallback((e: React.MouseEvent) => {
    // Only start panning if clicking on the base image and zoomed in
    if (canvasState.zoom > 1 && e.target instanceof HTMLImageElement) {
      e.preventDefault();
      setIsPanning(true);
      setPanStart({ 
        x: e.clientX - canvasState.panX, 
        y: e.clientY - canvasState.panY 
      });
    }
  }, [canvasState.zoom, canvasState.panX, canvasState.panY]);

  const handleTouchStart = useCallback((e: React.TouchEvent) => {
    // Only start panning if touching the base image and zoomed in
    if (canvasState.zoom > 1 && e.target instanceof HTMLImageElement) {
      e.preventDefault();
      setIsPanning(true);
      const touch = e.touches[0];
      setPanStart({ 
        x: touch.clientX - canvasState.panX, 
        y: touch.clientY - canvasState.panY 
      });
    }
  }, [canvasState.zoom, canvasState.panX, canvasState.panY]);

  const handleMouseMove = useCallback((e: MouseEvent) => {
    if (isPanning) {
      const newPanX = e.clientX - panStart.x;
      const newPanY = e.clientY - panStart.y;
      onPanChange(newPanX, newPanY);
    }
  }, [isPanning, panStart, onPanChange]);

  const handleTouchMove = useCallback((e: TouchEvent) => {
    if (isPanning) {
      e.preventDefault(); // Prevent scrolling
      const touch = e.touches[0];
      const newPanX = touch.clientX - panStart.x;
      const newPanY = touch.clientY - panStart.y;
      onPanChange(newPanX, newPanY);
    }
  }, [isPanning, panStart, onPanChange]);

  const handleMouseUp = useCallback(() => {
    setIsPanning(false);
  }, []);

  const handleTouchEnd = useCallback(() => {
    setIsPanning(false);
  }, []);

  // Add event listeners for mouse and touch move/up/end
  React.useEffect(() => {
    if (isPanning) {
      document.addEventListener('mousemove', handleMouseMove);
      document.addEventListener('mouseup', handleMouseUp);
      document.addEventListener('touchmove', handleTouchMove, { passive: false });
      document.addEventListener('touchend', handleTouchEnd);
      return () => {
        document.removeEventListener('mousemove', handleMouseMove);
        document.removeEventListener('mouseup', handleMouseUp);
        document.removeEventListener('touchmove', handleTouchMove);
        document.removeEventListener('touchend', handleTouchEnd);
      };
    }
  }, [isPanning, handleMouseMove, handleMouseUp, handleTouchMove, handleTouchEnd]);

  const handleZoomIn = () => {
    onZoomChange(Math.min(canvasState.zoom * 1.2, 3));
  };

  const handleZoomOut = () => {
    onZoomChange(Math.max(canvasState.zoom / 1.2, 0.1));
  };

  const handleBaseImageClick = (e: React.MouseEvent) => {
    e.preventDefault(); // Prevent mobile zoom on double-tap
    e.stopPropagation();
    // Select base image (deselect any overlays)
    onSelectOverlay(null);
  };

  const handleZoomInputChange = (value: string) => {
    const numValue = parseInt(value);
    if (!isNaN(numValue) && numValue > 0 && numValue <= 500) {
      onZoomChange(numValue / 100);
      setZoomInput(value);
    }
  };

  const handleZoomInputSubmit = () => {
    setIsEditingZoom(false);
    const numValue = parseInt(zoomInput);
    if (!isNaN(numValue) && numValue > 0 && numValue <= 500) {
      onZoomChange(numValue / 100);
    } else {
      setZoomInput(Math.round(canvasState.zoom * 100).toString());
    }
  };



  return (
    <main className="flex-1 bg-muted dark:bg-black flex flex-col">
      <div className="bg-background border-b border-border px-2 lg:px-4 py-2 lg:py-3 h-[7.5vh] lg:h-auto flex items-center">
        <div className="flex items-center justify-center lg:justify-end flex-wrap gap-2">
          <div className="flex items-center space-x-1 lg:space-x-2">
            <Button
              variant="ghost"
              size="sm"
              onMouseDown={(e) => {
                e.preventDefault();
                e.stopPropagation();
              }}
              onClick={(e) => {
                e.preventDefault();
                e.stopPropagation();
                onUndo();
              }}
              disabled={!canUndo}
              className="flex items-center space-x-1 text-xs md:text-sm"
              title="Undo last action (Ctrl+Z)"
            >
              <Undo className="h-4 w-4" />
              <span className="hidden sm:inline">Undo</span>
            </Button>
            <div className="h-4 w-px bg-border"></div>
            <div className="flex items-center space-x-1 md:space-x-2 text-xs md:text-sm text-muted-foreground">
              <span>Zoom:</span>
              <Button variant="ghost" size="sm" className="p-1" onClick={handleZoomOut}>
                <ZoomOut className="h-4 w-4" />
              </Button>
              {isEditingZoom ? (
                <Input
                  type="number"
                  value={zoomInput}
                  onChange={(e) => setZoomInput(e.target.value)}
                  onBlur={handleZoomInputSubmit}
                  onKeyDown={(e) => {
                    if (e.key === 'Enter') {
                      handleZoomInputSubmit();
                    } else if (e.key === 'Escape') {
                      setIsEditingZoom(false);
                      setZoomInput(Math.round(canvasState.zoom * 100).toString());
                    }
                  }}
                  className="w-16 h-6 text-xs text-center"
                  min="10"
                  max="500"
                  autoFocus
                />
              ) : (
                <span 
                  className="font-medium cursor-pointer hover:bg-gray-100 px-2 py-1 rounded"
                  onClick={() => setIsEditingZoom(true)}
                >
                  {Math.round(canvasState.zoom * 100)}%
                </span>
              )}
              <Button variant="ghost" size="sm" className="p-1" onClick={handleZoomIn}>
                <ZoomIn className="h-4 w-4" />
              </Button>
            </div>
            <div className="h-4 w-px bg-gray-300"></div>
            <Button
              variant="outline"
              onClick={() => fileInputRef.current?.click()}
              className="whitespace-nowrap text-xs lg:text-sm px-2 lg:px-3 py-1 lg:py-2"
            >
              <Upload className="mr-1 lg:mr-2 h-3 lg:h-4 w-3 lg:w-4" />
              <span className="hidden sm:inline">Upload Tank</span>
              <span className="sm:hidden">Upload</span>
            </Button>
            <input
              ref={fileInputRef}
              type="file"
              accept="image/*,.heic,.heif"
              onChange={handleFileUpload}
              className="hidden"
            />
          </div>
        </div>
      </div>

      <div className="flex-1 p-1 lg:p-3 overflow-auto">
        <div className="max-w-4xl mx-auto">
          <div
            ref={(node) => {
              drop(node);
              if (canvasRef.current !== node) {
                (canvasRef as React.MutableRefObject<HTMLDivElement | null>).current = node;
              }
            }}
            className={`canvas-workspace relative bg-background rounded-xl shadow-lg ${
              canvasState.baseImage ? '' : 'border-2 border-dashed border-border min-h-[600px]'
            } flex items-center justify-center overflow-hidden ${isOver ? 'drop-zone-active' : ''}`}
            onClick={handleCanvasClick}
            onWheel={(e) => e.preventDefault()}
            onTouchMove={(e) => e.preventDefault()}
            style={{ touchAction: 'none' }}
          >
            {canvasState.baseImage ? (
              <div 
                className="relative flex items-center justify-center border-2 border-dashed border-border rounded-lg"
                style={{
                  width: 'fit-content',
                  height: 'fit-content',
                  transform: `scale(${canvasState.zoom}) translate(${canvasState.panX / canvasState.zoom}px, ${canvasState.panY / canvasState.zoom}px)`,
                  transformOrigin: canvasState.zoom < 1 ? 'top center' : 'center',
                }}
              >
                <img
                  src={canvasState.baseImage}
                  alt="Tank base"
                  className={`rounded-lg block ${canvasState.zoom > 1 ? 'cursor-grab active:cursor-grabbing' : 'cursor-pointer'}`}
                  onClick={handleBaseImageClick}
                  onMouseDown={handleMouseDown}
                  onTouchStart={handleTouchStart}
                  style={{ userSelect: 'none', pointerEvents: 'auto', touchAction: 'none' }}
                />

                {/* Render overlays */}
                {canvasState.overlays.map((overlay) => (
                  <DraggableOverlay
                    key={overlay.id}
                    overlay={overlay}
                    isSelected={overlay.id === canvasState.selectedOverlayId}
                    onUpdate={(updates) => onUpdateOverlay(overlay.id, updates)}
                    onSelect={() => onSelectOverlay(overlay.id)}
                    onDelete={() => onDeleteOverlay(overlay.id)}
                    onDragStart={() => onDragStart(overlay.id)}
                    onDragEnd={onDragEnd}
                    zoom={canvasState.zoom}
                  />
                ))}

                {/* Watermark - sticky to tank image */}
                {canvasState.baseImage && (
                  <div 
                    className="watermark absolute z-50 pointer-events-none"
                    style={{ 
                      width: '20%',
                      bottom: '20px',
                      right: '20px',
                    }}
                  >
                    <img 
                      src="https://i.ibb.co/KcqLs8LM/Screen-Shot-2025-07-27-at-8-11-42-PM.png"
                      alt="CoralScape"
                      className="w-full h-auto opacity-80"
                      onError={(e) => {
                        const target = e.target as HTMLImageElement;
                        target.style.display = 'none';
                        const parent = target.parentElement;
                        if (parent) {
                          parent.innerHTML = '<div class="text-xs font-medium text-gray-600 px-2 py-1">CoralScape</div>';
                        }
                      }}
                    />
                  </div>
                )}
              </div>
            ) : (
              <div className="text-center p-12">
                <div className="text-muted-foreground mb-4">
