          </TabsContent>
        </Tabs>
      </div>

      <ScrollArea className={`flex-1 ${isMobileCompact ? 'p-1' : 'p-3 md:p-4'}`}>
        <Tabs defaultValue="database" className="w-full">
          <TabsContent value="database" className="mt-0">
            {isLoading ? (
