import { CanvasState, OverlayData } from "@shared/schema";
import { fetchWatermarkFromSheets } from "./google-sheets";

// Function to save image to camera roll (mobile)
export async function saveToPhotos(canvasState: CanvasState): Promise<void> {
  return new Promise((resolve, reject) => {
    const canvas = document.createElement('canvas');
    const ctx = canvas.getContext('2d');

    if (!ctx || !canvasState.baseImage) {
      reject(new Error('Canvas context or base image not available'));
      return;
    }

    const baseImg = new Image();
    baseImg.crossOrigin = 'anonymous';

    baseImg.onload = async () => {
      try {
        canvas.width = baseImg.width;
        canvas.height = baseImg.height;
        ctx.drawImage(baseImg, 0, 0);

        const overlayPromises = canvasState.overlays
          .sort((a, b) => a.layer - b.layer)
          .map(overlay => drawOverlay(ctx, overlay, canvas.width, canvas.height));

        await Promise.all(overlayPromises);
        await drawWatermark(ctx, canvas.width, canvas.height);

        // Convert to blob and save to photos
        canvas.toBlob(async (blob) => {
          if (blob) {
            try {
              // Create a file from the blob
              const file = new File([blob], `coralscape-design-${Date.now()}.png`, { type: 'image/png' });

              // Check if Web Share API is available
              if (navigator.share && navigator.canShare && navigator.canShare({ files: [file] })) {
                await navigator.share({
                  files: [file],
                  title: 'CoralScape Design',
                  text: 'My aquarium design from CoralScape'
                });
                resolve();
              } else {
                // Fallback to download
                const url = URL.createObjectURL(blob);
                const link = document.createElement('a');
                link.href = url;
                link.download = `coralscape-design-${Date.now()}.png`;
                document.body.appendChild(link);
                link.click();
                document.body.removeChild(link);
                URL.revokeObjectURL(url);
                resolve();
              }
            } catch (error) {
              reject(error);
            }
          } else {
            reject(new Error('Failed to create blob'));
          }
        }, 'image/png');

      } catch (error) {
        reject(error);
      }
    };

    baseImg.onerror = () => reject(new Error('Failed to load base image'));
    baseImg.src = canvasState.baseImage;
  });
}

export async function exportCanvasAsImage(canvasState: CanvasState): Promise<void> {
  return new Promise((resolve, reject) => {
    const canvas = document.createElement('canvas');
    const ctx = canvas.getContext('2d');

    if (!ctx || !canvasState.baseImage) {
      reject(new Error('Canvas context or base image not available'));
      return;
    }

    const baseImg = new Image();
    baseImg.crossOrigin = 'anonymous';

    baseImg.onload = async () => {
      try {
        // Set canvas dimensions based on base image
        canvas.width = baseImg.width;
        canvas.height = baseImg.height;

        // Draw base image
        ctx.drawImage(baseImg, 0, 0);

        // Draw overlays
        const overlayPromises = canvasState.overlays
          .sort((a, b) => a.layer - b.layer) // Draw in layer order
          .map(overlay => drawOverlay(ctx, overlay, canvas.width, canvas.height));

        await Promise.all(overlayPromises);

        // Draw watermark from Google Sheets
        await drawWatermark(ctx, canvas.width, canvas.height);

        // Export as blob and download
        canvas.toBlob((blob) => {
          if (blob) {
            const url = URL.createObjectURL(blob);
            const link = document.createElement('a');
            link.href = url;
            link.download = `coralscape-design-${Date.now()}.png`;
            document.body.appendChild(link);
            link.click();
            document.body.removeChild(link);
            URL.revokeObjectURL(url);
            resolve();
          } else {
            reject(new Error('Failed to create blob'));
          }
        }, 'image/png');

      } catch (error) {
        reject(error);
      }
    };

    baseImg.onerror = () => {
      reject(new Error('Failed to load base image'));
    };

    baseImg.src = canvasState.baseImage;
  });
}

function drawOverlay(
  ctx: CanvasRenderingContext2D, 
  overlay: OverlayData, 
  canvasWidth: number, 
  canvasHeight: number
): Promise<void> {
  return new Promise((resolve, reject) => {
    const img = new Image();
    img.crossOrigin = 'anonymous';

    img.onload = () => {
      try {
        ctx.save();
        ctx.globalAlpha = overlay.opacity;

        // Get the actual base image dimensions from the DOM
        const baseImageElement = document.querySelector('.canvas-workspace img') as HTMLImageElement;
        const baseImageRect = baseImageElement?.getBoundingClientRect();
        const displayWidth = baseImageRect?.width || canvasWidth;
        const displayHeight = baseImageRect?.height || canvasHeight;

        // Calculate position and size relative to canvas using actual displayed image dimensions
        const x = (overlay.x / displayWidth) * canvasWidth;
        const y = (overlay.y / displayHeight) * canvasHeight;
        const width = (overlay.width / displayWidth) * canvasWidth;
        const height = (overlay.height / displayHeight) * canvasHeight;

        // Apply transforms if they exist
        const centerX = x + width / 2;
        const centerY = y + height / 2;

        ctx.translate(centerX, centerY);

        // Apply rotation
        if (overlay.rotation) {
          ctx.rotate((overlay.rotation * Math.PI) / 180);
        }

        // Apply flips
        ctx.scale(overlay.flipH ? -1 : 1, overlay.flipV ? -1 : 1);

        // Draw the image centered
        ctx.drawImage(img, -width / 2, -height / 2, width, height);

        ctx.restore();
        resolve();
      } catch (error) {
        reject(error);
      }
    };

    img.onerror = () => {
      console.warn(`Failed to load overlay image: ${overlay.imageUrl}`);
      resolve(); // Continue even if overlay fails
    };

    img.src = overlay.imageUrl;
  });
}

async function drawWatermark(ctx: CanvasRenderingContext2D, canvasWidth: number, canvasHeight: number): Promise<void> {
  return new Promise(async (resolve) => {
    ctx.save();

    // Use the specific CoralScape watermark image
    const watermarkImageUrl = "https://i.ibb.co/KcqLs8LM/Screen-Shot-2025-07-27-at-8-11-42-PM.png";

    // Calculate watermark size (30% on mobile, 20% on desktop)
    const isMobile = window.innerWidth <= 1024;
    const watermarkWidth = canvasWidth * (isMobile ? 0.3 : 0.2);

    // Position in bottom right
    const x = canvasWidth - watermarkWidth - 20;

    try {
      const img = new Image();
      img.crossOrigin = 'anonymous';

      img.onload = () => {
        // Calculate proportional height
        const aspectRatio = img.width / img.height;
        const watermarkHeight = watermarkWidth / aspectRatio;
        const y = canvasHeight - watermarkHeight - 20;

        // Draw watermark image without background
        ctx.globalAlpha = 0.8;
        ctx.drawImage(img, x, y, watermarkWidth, watermarkHeight);
        ctx.globalAlpha = 1;

        ctx.restore();
        resolve();
      };

      img.onerror = () => {
        // Fall back to text if image fails
        drawTextWatermark();
      };

      img.src = watermarkImageUrl;
    } catch (error) {
      drawTextWatermark();
    }

    function drawTextWatermark() {
      const fontSize = Math.max(14, watermarkWidth / 8);
      const textY = canvasHeight - 40;

      // Draw text without background
      ctx.fillStyle = 'rgba(0, 0, 0, 0.7)';
      ctx.font = `bold ${fontSize}px Inter, sans-serif`;
      ctx.textAlign = 'center';
      ctx.fillText('CoralScape', x + watermarkWidth / 2, textY);

      ctx.restore();
      resolve();
    }
  });
}

export function loadImage(src: string): Promise<HTMLImageElement> {
  return new Promise((resolve, reject) => {
    const img = new Image();
    img.crossOrigin = 'anonymous';
    img.onload = () => resolve(img);
    img.onerror = reject;
    img.src = src;
  });
}

client/src/lib/google-sheets.ts.content:
export interface GoogleSheetsRow {
  name: string;
  fullImageUrl: string;
  thumbnailUrl: string;
  width: number;
  height: number;
}

export function parseGoogleSheetsUrl(url: string): string | null {
  const match = url.match(/\/spreadsheets\/d\/([a-zA-Z0-9-_]+)/);
  return match ? match[1] : null;
}

export function buildCsvUrl(sheetId: string, gid?: string): string {
  const baseUrl = `https://docs.google.com/spreadsheets/d/${sheetId}/export?format=csv`;
  return gid ? `${baseUrl}&gid=${gid}` : baseUrl;
}

export function parseCsvData(csvData: string): GoogleSheetsRow[] {
  const rows = csvData.split('\n').slice(1); // Skip header row
  const parsedData: GoogleSheetsRow[] = [];

  for (const row of rows) {
    if (row.trim()) {
      const columns = row.split(',').map(col => col.trim().replace(/"/g, ''));

      if (columns.length >= 6) {
        const rowData: GoogleSheetsRow = {
          name: columns[1] || '',
          fullImageUrl: columns[2] || '',
          thumbnailUrl: columns[3] || '',
          width: parseInt(columns[4]) || 0,
          height: parseInt(columns[5]) || 0,
        };

        // Validate required fields
        if (rowData.name && rowData.fullImageUrl && rowData.thumbnailUrl) {
          parsedData.push(rowData);
        }
      }
    }
  }

  return parsedData;
}

export async function fetchGoogleSheetsData(url: string): Promise<GoogleSheetsRow[]> {
  const sheetId = parseGoogleSheetsUrl(url);

  if (!sheetId) {
    throw new Error('Invalid Google Sheets URL format');
  }

  const csvUrl = buildCsvUrl(sheetId);

  try {
    const response = await fetch(csvUrl);

    if (!response.ok) {
      throw new Error(`Failed to fetch data: ${response.status} ${response.statusText}`);
    }

    const csvData = await response.text();
    return parseCsvData(csvData);

  } catch (error) {
    if (error instanceof Error) {
      throw new Error(`Google Sheets connection failed: ${error.message}`);
    }
    throw new Error('Unknown error occurred while fetching Google Sheets data');
  }
}

export async function fetchWatermarkFromSheets(url: string): Promise<string | null> {
  const sheetId = parseGoogleSheetsUrl(url);

  if (!sheetId) {
    throw new Error('Invalid Google Sheets URL format');
  }

  // For sheet 2, we need to use a specific gid. Common gids for sheet 2 are "0" for sheet 1, and various numbers for subsequent sheets
  // We'll try different approaches to get sheet 2
  const possibleGids = ['1', '2000000000', '1555555555', '669674685']; // Common gids for second sheets

  for (const gid of possibleGids) {
    try {
      const csvUrl = buildCsvUrl(sheetId, gid);
      const response = await fetch(csvUrl);

      if (response.ok) {
        const csvData = await response.text();
        const rows = csvData.split('\n');

        if (rows.length > 0) {
          const firstRow = rows[0];
          const columns = firstRow.split(',').map(col => col.trim().replace(/"/g, ''));

          if (columns.length > 0 && columns[0]) {
            // Check if it's an image URL (http/https)
            const value = columns[0];
            if (value.startsWith('http://') || value.startsWith('https://')) {
              return value; // Return the image URL from column A, row 1
            } else {
              return value; // Return as text if not an image URL
            }
          }
        }
      }
    } catch (error) {
      continue; // Try next gid
    }
  }

  return null; // No watermark found
}

client/src/lib/heic-converter.ts.content:
import heic2any from 'heic2any';

/**
 * Converts HEIC files to JPEG format for web compatibility
 * @param file - The HEIC file to convert
 * @returns Promise<string> - Data URL of the converted JPEG image
 */
export async function convertHeicToJpeg(file: File): Promise<string> {
  try {
    // Check if the file is HEIC
    const isHeic = file.type === 'image/heic' || 
                   file.type === 'image/heif' || 
                   file.name.toLowerCase().endsWith('.heic') || 
                   file.name.toLowerCase().endsWith('.heif');

    if (!isHeic) {
      // If not HEIC, return the original file as data URL
      return new Promise((resolve, reject) => {
        const reader = new FileReader();
        reader.onload = (e) => resolve(e.target?.result as string);
        reader.onerror = reject;
        reader.readAsDataURL(file);
      });
    }

    console.log('Converting HEIC file:', file.name);

    // Convert HEIC to JPEG
    const convertedBlob = await heic2any({
      blob: file,
      toType: 'image/jpeg',
      quality: 0.9
    });

    // Handle both single blob and array of blobs
    const blob = Array.isArray(convertedBlob) ? convertedBlob[0] : convertedBlob;

    // Convert blob to data URL
    return new Promise((resolve, reject) => {
      const reader = new FileReader();
      reader.onload = (e) => resolve(e.target?.result as string);
      reader.onerror = reject;
      reader.readAsDataURL(blob);
    });

  } catch (error) {
    console.error('Error converting HEIC file:', error);
    throw new Error('Failed to convert HEIC file. Please try a different image format.');
  }
}

/**
 * Checks if a file is a supported image format (including HEIC)
 * @param file - The file to check
 * @returns boolean - True if the file is a supported image format
 */
export function isSupportedImageFile(file: File): boolean {
  const supportedTypes = [
    'image/jpeg',
    'image/jpg', 
    'image/png',
    'image/gif',
    'image/webp',
    'image/heic',
    'image/heif'
  ];

  const supportedExtensions = ['.jpg', '.jpeg', '.png', '.gif', '.webp', '.heic', '.heif'];

  return supportedTypes.includes(file.type) || 
         supportedExtensions.some(ext => file.name.toLowerCase().endsWith(ext));
}

